    const [note,updateNote]=React.useState({
        title:"",
        content:""
    })

    function addNote(noteObj){
        updateNote(noteObj);
    }

    return(
        <div>
        <Header/>
        <Create 
            onClick={addNote}
        />
        {/* {notes.map(function (x){
            return (
                <notes title={x.title} msg={x.content} key={x.key}/>
            )
        })} */}
       {note.map((x)=>{
        <N title={x.title} msg={x.content}/>
       })}
        <Footer/>
        </div>
    ) 
}

export default App
